    private void MaximizeIconMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_MaximizeIconMouseClicked
        if(this.getExtendedState()!= LoadingWindow.MAXIMIZED_BOTH){
            this.setExtendedState(LoadingWindow.MAXIMIZED_BOTH);       
        }
        else{
            this.setExtendedState(LoadingWindow.NORMAL);
        }
    }//GEN-LAST:event_MaximizeIconMouseClicked

