    public static void main(String args[]) {
        
        
        /* Set the Nimbus look and feel */
        //<editor-fold defaultstate="collapsed" desc=" Look and feel setting code (optional) ">
        /* If Nimbus (introduced in Java SE 6) is not available, stay with the default look and feel.
         * For details see http://download.oracle.com/javase/tutorial/uiswing/lookandfeel/plaf.html 
         */
        try {
            for (javax.swing.UIManager.LookAndFeelInfo info : javax.swing.UIManager.getInstalledLookAndFeels()) {
                //System.out.println(info.getClassName());
                if ("Nimbus".equals(info.getName())) {
                    javax.swing.UIManager.setLookAndFeel(info.getClassName());
                  //  System.out.println(info.getClassName());
                    break;
                }
            }
        } catch (ClassNotFoundException | InstantiationException | IllegalAccessException | javax.swing.UnsupportedLookAndFeelException ex) {
            java.util.logging.Logger.getLogger(LoadingWindow.class.getName()).log(java.util.logging.Level.SEVERE, null, ex);
        }
        
        /* Create and display the form */
        LoadingWindow ls = new LoadingWindow();
        ls.setVisible(true);
        Login log =new Login();
        try{
            for (int i=0;i<=100;i++) {
                int SleepTime = 100;
                Thread.sleep(SleepTime);
                ls.LoadingValue.setText(i + "%");
                ls.LoadingBar.setValue(i);
                if (i==10) {
                    ls.LoadingLabel.setText("Prepraing Modules...");
                }
                if (i==20) {
                    ls.LoadingLabel.setText("Turning on Modules...");
                }
                if (i==30) {
                    ls.LoadingLabel.setText("Loading Modules...");
                }
                if (i==50) {
                    ls.LoadingLabel.setText("Connecting to Server...");
                }
                if (i==70) {
                    ls.LoadingLabel.setText("Connection Established!");
                }
                if (i==80) {
                    ls.LoadingLabel.setText("Preparation For Lauanching...");
                }
                if (i==90) {
                    ls.LoadingLabel.setText("Launching Application...");
                }  
                if (i==100) {
                    boolean flase = false;
                    ls.setVisible(flase);
                    ls.dispose();
                }
            }
        }
        catch(InterruptedException e){
            System.err.println(e);
            JOptionPane.showMessageDialog(null,"System Is Crashing" , "Error Message", JOptionPane.ERROR_MESSAGE);
        }
         log.setVisible(true);
         ls.dispose();
    }
    


